package com.clover.cfp.examples;

import android.content.res.Configuration;
import android.os.Bundle;
import android.webkit.WebView;
import android.webkit.WebViewClient;

import com.clover.cfp.activity.CFPConstants;
import com.clover.cfp.examples.R;
import com.clover.cfp.activity.CloverCFPActivity;

import com.google.gson.Gson;

import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class WebViewActivity extends CloverCFPActivity {

  private WebView webView;

  @Override protected void onCreate(Bundle savedInstanceState) {
    super.onCreate(savedInstanceState);
    setContentView(R.layout.activity_web_view);

    webView = (WebView)findViewById(R.id.webView);
//    AndroidBug5497Workaround.assistActivity(this);

  }

  @Override
  protected void onMessage(String payload) {
    // implement if doing conversational activities
    WebViewRequest wvr = new Gson().fromJson(payload, WebViewRequest.class);


    webView.loadData(wvr.html, "text/html", "UTF-8");



  }

  @Override protected void onResume() {
    super.onResume();
    String payload = getIntent().getStringExtra(CFPConstants.EXTRA_PAYLOAD);


    String htmlContent = null;

    if(getIntent().getAction().equals(receiver) && payload != null) {
      onMessage(payload);
    }
    else {
      int orientation = this.getResources().getConfiguration().orientation;
      if(orientation == Configuration.ORIENTATION_PORTRAIT){
        htmlContent = "<html><div style=\"height:100%;display:flex;flex-direction:column;justify-content:center;align-items:center;\"><img width=\"300px\" src=\"data:image/png;base64,\"/>";
        htmlContent += "<div style=\"display:flex;flex-direction:row;justify-content:center;font-size:20px;padding-top:30px;\">";
        htmlContent += "<form action=\"cfp://finish\" method=\"GET\" style=\"display:flex;flex-direction:column;justify-content:center;align-items:center;\">";
        htmlContent += "<strong>Return payload: </strong><input type=\"text\" name=\"resultPayload\" style=\"font-size:18px; padding:10px;width:300px;margin-top:10px\">";
        htmlContent += "<input type=\"submit\" value=\"Finish\" style=\"background:white;border: 1px solid black; padding:10px 50px; border-radius: 5px; font-size:16px;margin-top:10px\">";
        htmlContent += "</form>";
        htmlContent += "</div>";
        htmlContent += "</div>";
        htmlContent += "</html>";
      }
      else {
        htmlContent = "<html><div style=\"height:100%;display:flex;flex-direction:column;justify-content:center;align-items:center;\"><img src=\"data:image/png;base64,\"/>";
        htmlContent += "<div style=\"display:flex;flex-direction:row;justify-content:center;font-size:30px;margin-top:20px;\">";
        htmlContent += "<form action=\"cfp://finish\" method=\"GET\" style=\"display:flex;flex-direction:row;\">";
        htmlContent += "Return payload: <input type=\"text\" name=\"resultPayload\" style=\"font-size:18px; padding:10px; margin-left: 10px; margin-right: 10px;\">";
        htmlContent += "<input type=\"submit\" value=\"Finish\" style=\"background: white;border: 1px solid black; padding:10px 50px; border-radius: 5px; font-size:18px\">";
        htmlContent += "</form>";
        htmlContent += "</div>";
        htmlContent += "</div>";
        htmlContent += "</html>";
      }

      webView.loadData(htmlContent, "text/html", "UTF-8");

      webView.setWebViewClient(new WebViewClient(){
        @Override public boolean shouldOverrideUrlLoading(WebView view, String url) {
          if(url.startsWith("cfp://finish")) {
            String regex = "resultPayload=(\\S*)";
            Pattern pattern = Pattern.compile(regex);
            Matcher matcher = pattern.matcher(url);

            String result = "";
            while(matcher.find()){
              result = matcher.group(1);
            }
            setResultAndFinish(RESULT_OK, result);
            return true; // we will handle it...
          } else {
            return super.shouldOverrideUrlLoading(view, url);
          }
        }
      });
    }



  }

  class WebViewRequest {
    String html;
    String url;
  }
}
